import React from 'react'
import { makeStyles } from '@material-ui/styles'
import Grid from '@material-ui/core/Grid'
import Typography from '@material-ui/core/Typography'
import clsx from 'clsx'

import WidthContainer from '../common/WidthContainer'
// import HeroCard from './HeroCard'
// import LinkField from '../common/LinkField'
// import config from '../config'

const useStyles = makeStyles(theme => ({
  root: {
    padding: '45px 0 0 0',
    background: `url(${config.MEDIA_CDN_DOMAIN}/media/hero.jpg) top center no-repeat rgb(217, 217, 217)`,
    display: 'flex',
    backgroundSize: 'cover',
    flexDirection: 'column'
  },
  container: {
    [theme.breakpoints.down('xs')]: {
      padding: 25
    }
  },
  heroOverlay: {
    backgroundColor: 'rgba(36, 109, 117, .9)',
    color: theme.palette.background.default,
    textAlign: 'left',
    padding: 30,
    [theme.breakpoints.down('xs')]: {
      padding: 25
    }
  },
  heroTitle: {
    [theme.breakpoints.down('xs')]: {
      fontSize: '3.0rem'
    }
  },
  heroCards: {
    backgroundColor: 'rgba(3, 107, 107, 0.5)',
    padding: '30px 0',
    marginTop: 40,
    [theme.breakpoints.down('xs')]: {
      marginTop: 150
    }
  },
  subTitle: {
    marginBottom: '15px'
  },
  gridItem: {
    padding: '20px 30px !important',
    [theme.breakpoints.down('xs')]: {
      padding: '0px !important',
      marginBottom: 2
    }
  },
  gridItemLeft: {
    paddingLeft: '20px !important',
    [theme.breakpoints.down('xs')]: {
      padding: '0px !important'
    }
  },
  gridItemRight: {
    paddingRight: '20px !important',
    [theme.breakpoints.down('xs')]: {
      padding: '0px !important'
    }
  }
}))

function Hero() {
  const classes = useStyles()

  return (
    <div className={classes.root}>
      <WidthContainer fullWidth={true} className={classes.columnWrapper}>
        <Grid container spacing={0} className={classes.container}>
          <Grid item md={4} xs={12} className={classes.heroOverlay}>
            <Typography variant="h2" paragraph className={classes.heroTitle}>
              Connect. Build.
            </Typography>
            <Typography variant="body2" className={classes.subTitle}>
              TIP Exchange brings together the ecosystem to drive new connectivity solutions to
              market.
            </Typography>
          </Grid>
        </Grid>
      </WidthContainer>
    </div>
  )
}

export default Hero
